package esercizio6;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		{
            for (int i = 0; i < 100; i++) {
                System.out.println(i);
            }
        }
        {
            int i=0;
            while (i<100) {
                System.out.println(i);
                i = i+1;
            }
        }
        {
            int i=0;
            do {
                System.out.println(i);
                i = i+1;
            } while (i<100);
        }
		

	}

}
